/* eslint-disable no-restricted-syntax */
// Export two functions that either saves the AST state object into an array
// or returns the array for use
const masterState = [];

module.exports = {
  saveState: state => {
    //* State: {_useState: "count", _useState2: "setCount", _useState3: "test", _useState4: "test2"}
    for (const key in state) {
      masterState.push(state[key]);
    }
    //* masterState: ["count", "setCount", "test", "test2"]
    return masterState;
  },
  //* Return the masterState array
  returnState: () => masterState,
};